        i0.ɵɵelement(365, "br");
        i0.ɵɵtext(366, " Time: Mon \u2013 Sat (9:00 \u2013 18:00 hrs) ");
        i0.ɵɵelement(367, "br");
        i0.ɵɵelementStart(368, "a", 11);
        i0.ɵɵtext(369, "grievance@clorev.com");
        i0.ɵɵelementEnd()();
        i0.ɵɵelementStart(370, "p")(371, "b");
        i0.ɵɵtext(372, "8.2)");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(373, "\u00A0 \u00A0\u00A0Further, please be noted that the Platform stores your data on the cloud platform of Amazon Web Services, Inc., which may store this data on their servers located outside of India. Amazon Web Services has security measures in place to protect the loss, misuse and alteration of the information (refer ");
        i0.ɵɵelementStart(374, "a", 12);
